import{_ as t,a,b as o}from"./chunks/3.CPmdNuYA.js";import{_ as r,c as i,o as l,ae as s}from"./chunks/framework.tuuL-SzM.js";const q=JSON.parse('{"title":"AI+ Prompt Management Plugin User Manual","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"guide/features/home.md","filePath":"guide/features/home.md","lastUpdated":1744790304000}'),n={name:"guide/features/home.md"};function p(m,e,d,u,c,g){return l(),i("div",null,e[0]||(e[0]=[s('<h1 id="ai-prompt-management-plugin-user-manual" tabindex="-1">AI+ Prompt Management Plugin User Manual <a class="header-anchor" href="#ai-prompt-management-plugin-user-manual" aria-label="Permalink to &quot;AI+ Prompt Management Plugin User Manual&quot;">​</a></h1><h2 id="_1-product-overview" tabindex="-1">1. Product Overview <a class="header-anchor" href="#_1-product-overview" aria-label="Permalink to &quot;1. Product Overview&quot;">​</a></h2><p>The AI+ Prompt Management Plugin is a specialized tool designed for managing AI prompts, helping users efficiently create, edit, and manage prompt templates to enhance AI application productivity.</p><h2 id="_2-core-features" tabindex="-1">2. Core Features <a class="header-anchor" href="#_2-core-features" aria-label="Permalink to &quot;2. Core Features&quot;">​</a></h2><h3 id="_2-1-prompt-template-library" tabindex="-1">2.1 Prompt Template Library <a class="header-anchor" href="#_2-1-prompt-template-library" aria-label="Permalink to &quot;2.1 Prompt Template Library&quot;">​</a></h3><ul><li><strong>Description</strong>: Provides a preset library of prompt templates for quick selection and use. <img src="'+t+'" alt="Home"></li><li><strong>Usage</strong>: <ol><li>Search for the desired prompt card on the main interface.</li><li>Click &quot;Use&quot; to directly apply the template or <a href="#_2-2-prompt-template-usage-page">open parameter configuration page</a>.</li><li>Click &quot;Edit&quot; to <a href="#_2-3-add-prompt-template">customize the template</a>.</li></ol></li></ul><h3 id="_2-2-prompt-template-usage-page" tabindex="-1">2.2 Prompt Template Usage Page <a class="header-anchor" href="#_2-2-prompt-template-usage-page" aria-label="Permalink to &quot;2.2 Prompt Template Usage Page&quot;">​</a></h3><ul><li><strong>Description</strong>: Displays template details including role, description and objectives. <img src="'+a+'" alt="Home"></li><li><strong>Usage</strong>: <ol><li>View the complete template content.</li><li>Click &quot;Use&quot; to directly apply the template - it will insert the prompt into the AI product dialog while also copying it to clipboard for use elsewhere.</li></ol></li></ul><h3 id="_2-3-add-edit-prompt-template" tabindex="-1">2.3 Add/Edit Prompt Template <a class="header-anchor" href="#_2-3-add-edit-prompt-template" aria-label="Permalink to &quot;2.3 Add/Edit Prompt Template&quot;">​</a></h3><ul><li><strong>Description</strong>: Create new prompt templates. <img src="'+o+'" alt="Home"></li><li><strong>Usage</strong>: <ol><li>Click &quot;+&quot; button on top-right to enter &quot;Add Prompt Template&quot; page.</li><li>Enter template name in &quot;Title&quot; field.</li><li>Input prompt content in &quot;Content&quot; area (Markdown supported).</li><li>Click &quot;Template Examples&quot; to view sample templates.</li><li>Add tags in &quot;Tags&quot; field (press Enter to add).</li><li>Click &quot;Save&quot; to complete template creation.</li></ol></li></ul><h2 id="_3-feature-details" tabindex="-1">3. Feature Details <a class="header-anchor" href="#_3-feature-details" aria-label="Permalink to &quot;3. Feature Details&quot;">​</a></h2><h3 id="_3-1-prompt-template-library" tabindex="-1">3.1 Prompt Template Library <a class="header-anchor" href="#_3-1-prompt-template-library" aria-label="Permalink to &quot;3.1 Prompt Template Library&quot;">​</a></h3><ul><li><strong>Use</strong>: Directly apply templates.</li><li><strong>Edit</strong>: Modify template content.</li><li><strong>Favorite</strong>: Add templates to favorites.</li><li><strong>Delete</strong>: Remove unwanted templates.</li></ul><h3 id="_3-2-prompt-template-details" tabindex="-1">3.2 Prompt Template Details <a class="header-anchor" href="#_3-2-prompt-template-details" aria-label="Permalink to &quot;3.2 Prompt Template Details&quot;">​</a></h3><ul><li><strong>Role</strong>: Defines usage scenarios.</li><li><strong>Description</strong>: Provides detailed explanation.</li><li><strong>Objectives</strong>: Clarifies usage purposes.</li></ul><h3 id="_3-3-add-prompt-template" tabindex="-1">3.3 Add Prompt Template <a class="header-anchor" href="#_3-3-add-prompt-template" aria-label="Permalink to &quot;3.3 Add Prompt Template&quot;">​</a></h3><ul><li><strong>Title</strong>: Template name for quick identification.</li><li><strong>Content</strong>: Supports Markdown formatting with parameters (e.g. <code>[[param: description]]</code>).</li><li><strong>Tags</strong>: For categorization and search.</li></ul><h2 id="_4-notes" tabindex="-1">4. Notes <a class="header-anchor" href="#_4-notes" aria-label="Permalink to &quot;4. Notes&quot;">​</a></h2><ol><li><strong>Markdown Formatting</strong>: Content area supports Markdown for text styling.</li><li><strong>Parameterized Prompts</strong>: Supports inserting parameters (e.g. <code>[[param: description]]</code>) for dynamic adjustments.</li><li><strong>Template Examples</strong>: Click &quot;Template Examples&quot; for inspiration.</li></ol><h2 id="_5-faqs" tabindex="-1">5. FAQs <a class="header-anchor" href="#_5-faqs" aria-label="Permalink to &quot;5. FAQs&quot;">​</a></h2><ul><li><p><strong>How to edit saved templates?</strong><br> Click &quot;Edit&quot; in template list, modify and save.</p></li><li><p><strong>How to delete templates?</strong><br> Click &quot;Delete&quot; in template list.</p></li><li><p><strong>How to favorite templates?</strong><br> Click &quot;Favorite&quot; on template details page.</p></li></ul><h2 id="_6-support" tabindex="-1">6. Support <a class="header-anchor" href="#_6-support" aria-label="Permalink to &quot;6. Support&quot;">​</a></h2><p>For further assistance, contact technical support team. Email: <a href="mailto:threewang606@gmail.com" target="_blank" rel="noreferrer">threewang606@gmail.com</a></p>',23)]))}const _=r(n,[["render",p]]);export{q as __pageData,_ as default};
